package week1.day1;

public class ConditionalStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a=5;
	System.out.println(a>10);
	if (a>10) {
		System.out.println("A greater than 10");
	}
	else {
		System.out.println("A lesser than 10");
	}
}
}
